package adeln.json.json

import android.util.Log
import com.fasterxml.jackson.databind.DeserializationFeature
import com.fasterxml.jackson.databind.ObjectMapper
import com.fasterxml.jackson.module.kotlin.KotlinModule
import com.google.gson.Gson
import com.squareup.moshi.Moshi
import com.squareup.moshi.Types
import okhttp3.HttpUrl
import okhttp3.Request
import okhttp3.mockwebserver.MockWebServer
import okio.BufferedSource
import java.io.InputStreamReader
import java.lang.reflect.ParameterizedType

inline fun <T> mockWebServer(f: (MockWebServer) -> T): T =
    MockWebServer().use {
        it.start()
        f(it)
    }

fun mkRequest(url: HttpUrl): Request =
    Request.Builder().url(url).build()

fun testLog(x: Any): Int =
    Log.d("testlog", x.toString())

fun mkType(): ParameterizedType =
    Types.newParameterizedType(LfmResults::class.java, AlbumSearch::class.java)

typealias Parser = (BufferedSource) -> LfmResults<AlbumSearch>

fun mkMoshi(): Parser {
    val moshi = Moshi.Builder().build()

    val adapter = moshi.adapter<LfmResults<AlbumSearch>>(mkType())

    return {
        adapter.fromJson(it)!!
    }
}

fun mkGson(): Parser {
    val gson = Gson()

    val type = mkType()

    return {
        gson.fromJson(InputStreamReader(it.inputStream()), type)
    }
}

fun mkJackson(): Parser {
    val om = ObjectMapper()
        .registerModule(KotlinModule())
        .also {
            it.configure(DeserializationFeature.FAIL_ON_UNKNOWN_PROPERTIES, false)
        }

    val type = om.typeFactory.constructParametricType(LfmResults::class.java, AlbumSearch::class.java)

    return {
        om.readValue(it.inputStream(), type)
    }
}

